import { useCallback, useState } from 'react';
import ReactFlow, {
    MiniMap,
    Controls,
    Background,
    useNodesState,
    useEdgesState,
    addEdge,
} from 'reactflow';
// ðŸ‘‡ you need to import the reactflow styles
import 'reactflow/dist/style.css';
import './Editor.css';
import UsualNode from '../nodes/UsualNode/UsualNode';
import manyFromJson from '../nodes/fromJson';
import getProjectJson from '../api/getProjectJson';
import DebugSection from './DebugSection';
import Header from '../Header/Header';
import EditorToolbar from './EditorToolbar';
import SplitPane, { Pane } from 'split-pane-react';
import 'split-pane-react/esm/themes/default.css'

const nodeTypes = { usual: UsualNode };
const debug_input = getProjectJson();
const initialNodes = [...manyFromJson(debug_input)];
const initialEdges = [];
const [sizes, setSizes] = useState([
    100,
    '30%',
    'auto',
]);
const layoutCSS = {
    height: '100%',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
};

export default function Editor() {
    const [nodes, setNodes, onNodesChange] = useNodesState(initialNodes);
    const [edges, setEdges, onEdgesChange] = useEdgesState(initialEdges);
    const onConnect = useCallback(
        (connection) => { setEdges((eds) => addEdge(connection, eds)) },
        [setEdges]
    );

    return (
        <>
            <Header />
            <EditorToolbar />
            <div className='page-layout'>
                <SplitPane
                    split='vertical'
                    sizes={sizes}
                    onChange={setSizes}
                >
                    <Pane minSize={50} maxSize='50%'>
                        <DebugSection />
                    </Pane>
                    <div className="editor">
                        <ReactFlow
                            nodes={nodes}
                            edges={edges}
                            onNodesChange={onNodesChange}
                            onEdgesChange={onEdgesChange}
                            onConnect={onConnect}
                            nodeTypes={nodeTypes}
                        >
                            <MiniMap />
                            <Controls />
                            <Background />
                        </ReactFlow>
                    </div>
                </SplitPane>
            </div>
        </>
    );
}
