import { useCallback } from 'react';
import ReactFlow, {
    MiniMap,
    Controls,
    Background,
    useNodesState,
    useEdgesState,
    addEdge,
} from 'reactflow';
// ðŸ‘‡ you need to import the reactflow styles
import 'reactflow/dist/style.css';
import './Editor.css';
import UsualNode from '../nodes/UsualNode/UsualNode';
import manyFromJson from '../nodes/fromJson';

const nodeTypes = { usual: UsualNode };

const debug_input = "[{\"name\":\"_texture_0\",\"code\":\"vec4+_texture_0(vec2+coord){\\n\\treturn+texture2D(img0,+coord);\\n}\",\"id\":0,\"additionElements\":[\"uniform+sampler2D+img0;\"],\"parameters\":[],\"positionX\":413.2,\"positionY\":449,\"width\":200,\"height\":100,\"inputs\":[{\"type\":\"vec2\",\"name\":\"coord\",\"id\":0,\"connectedSource\":\"node2output0\"}],+\"parameters\":+[],+\"output\":+{\"type\":\"vec4\",\"name\":\"result\",\"id\":0}},+{\"name\":\"colorOutput\",\"code\":\"void+colorOutput(vec4+color){\\n\\tgl_FragColor+=+color;\\n}\",\"id\":1,\"additionElements\":[],\"parameters\":[],\"positionX\":726,\"positionY\":457,\"width\":200,\"height\":50,+\"inputs\":+[{\"type\":\"vec4\",\"name\":\"color\",\"id\":0,+\"connectedSource\":+\"node0output0\"}],+\"parameters\":+[],+\"output\":+{\"type\":\"void\",\"name\":\"result\",\"id\":0}},+{\"name\":\"texCoord_\",\"code\":\"vec2+texCoord_(){\\n\\treturn+texCoord;\\n}\",\"id\":2,\"additionElements\":[\"varying+vec2+texCoord;\"],\"parameters\":[],\"positionX\":137,\"positionY\":394,\"width\":200,\"height\":70,+\"inputs\":+[],+\"parameters\":+[],+\"output\":+{\"type\":\"vec2\",\"name\":\"result\",\"id\":0}}]"
const initialNodes = manyFromJson(debug_input);

const initialEdges = [];

export default function Editor() {
    const [nodes, setNodes, onNodesChange] = useNodesState(initialNodes);
    const [edges, setEdges, onEdgesChange] = useEdgesState(initialEdges);

    const onConnect = useCallback((params) => setEdges((eds) => addEdge(params, eds)), [setEdges]);

    const compile = () => {
        console.log("compile");
    }

    return (
        <>
            <button onClick={compile}>
                Compile
            </button>
            <div className="editor">
                <ReactFlow
                    nodes={nodes}
                    edges={edges}
                    onNodesChange={onNodesChange}
                    onEdgesChange={onEdgesChange}
                    onConnect={onConnect}
                    nodeTypes={nodeTypes}
                >
                    <MiniMap />
                    <Controls />
                    <Background />
                </ReactFlow>
            </div>
        </>
    );
}
